rule "Windows - Applocker - Enrichment 51"

when 
    to_string($message.event_provider) == "Microsoft-Windows-AppLocker" AND
    (
        to_long($message.event_code) >= 8002 AND
        to_long($message.event_code) <= 8007
    ) 
        OR
    ( 
        to_long($message.event_code) >= 8020 AND 
        to_long($message.event_code) <= 8025 
    )

then 
    let appcat = lookup_value(
        lookup_table: "windows-applocker-applicationwhitelist", 
        key: to_string($message.hash_sha256)
    );
    set_field (
        field: "application_category", 
        value: "UNDEFINED"
    );
    set_field (
        field: "application_category", 
        value: appcat
    ); 

end

rule "Windows - Applocker - Cleanup 93"

when
    to_string($message.event_provider) == "Microsoft-Windows-AppLocker" AND
    to_string($message.agent_type) == "winlogbeat"

then
    remove_field("^@.*");
    remove_field("^agent_.*");
    remove_field("beats_type");
    remove_field("^winlog_.*");
    remove_field("tags");
end

rule "Windows - Applocker - Enrichment 52"
when
    to_string($message.event_provider) == "Microsoft-Windows-AppLocker" AND
    has_field("source")

then
    let team = lookup_value(
        lookup_table: "windows-applocker-servermanagementlist", 
        key: to_string($message.source)
    );
    set_field (
        field: "application_team",
        value: "UNDEFINED"
    );
    set_field (
        field: "application_team", 
        value: team
    ); 

end

rule "Windows - Applocker - NXLog - Normalization 21"

when
    (
        to_string($message.SourceName) == "Microsoft-Windows-AppLocker" AND
        to_string($message.agent_type) == "nxlog"
    ) 

then
    set_field(
        field: "user_domain",
        value: to_string($message.Domain)
    );

    set_field(
        field: "event_code",
        value: to_long($message.EventID)
    );

    set_field(
        field: "user_name",
        value: to_string($message.AccountName)
    );

    set_field(
        field: "user_type",
        value: lowercase(to_string($message.AccountType))
    );

    set_field(
        field: "user_id",
        value: to_string($message.UserID)
    );

    set_field(
        field: "event_source",
        value: to_string($message.collector_node_id)
    );

    set_field(
        field: "event_provider",
        value: to_string($message.SourceName)
    );

    set_field(
        field: "event_channel",
        value: to_string($message.Channel)
    );

end

