rule "Windows - Applocker - Cleanup 92"

when
    to_string($message.event_provider) == "Microsoft-Windows-AppLocker" AND
    to_string($message.agent_type) == "nxlog"

then
    remove_field("[A-Z].*");
    remove_field("agent_type");
    remove_field("collector_node_id");
    remove_field("full_message");

end

rule "Windows - AppLocker - Parsing 33" 

when 
    to_string($message.event_provider) == "Microsoft-Windows-AppLocker" AND
    to_long($message.event_code) >= 8020 AND 
    to_long($message.event_code) <= 8025 

then
    let applocker = grok( 
        pattern: "%{WORD:file_base}[A-Z.]+%{WORD:application_name} %{GREEDYDATA}", 
        value: uppercase(to_string($message.message)), only_named_captures: true 
    ); 

    let apppath = grok( 
        pattern: "%{GREEDYDATA:file_path}[.]%{WORD} %{GREEDYDATA}", 
        value: uppercase(to_string($message.message)), only_named_captures: true 
    ); 

    set_field("file_extension", "APP");
    set_fields(apppath);
    set_fields(applocker); 

end

rule "Windows - AppLocker - Parsing 34" 

when 
    to_string($message.event_provider) == "Microsoft-Windows-AppLocker" AND
    to_long($message.event_code) >= 8002 AND 
    to_long($message.event_code) <= 8007 

then 
    let app = regex( 
        pattern: "(([\\%]*)([A-Z0-9]+|([\\\\\\\\]*[A-Za-z0-9.]+\\\\)[A-Za-z0-9\\$]+|[A-Z]:\\\\)([\\%]*)([\\\\\\sa-zA-Z0-9\\{\\}\\$_.-]*\\\\)+([\\sa-zA-Z0-9\\$_.-]+)([.]{1})([A-Z0-9]{3,6}))", 
        value: to_string($message.message) 
    ); 

    let appname = regex( 
        pattern:"(\\\\APPDATA\\\\LOCAL\\\\TEMP\\\\)", 
        value: to_string($message.message) 
    ); 

    set_field("file_path",app["0"]);
    set_field("file_base",app["2"]);
    set_field("application_name",app["6"]);
    set_field("file_extension",app["8"]);

    set_field("application_name", replace(to_string(appname["0"]), to_string(appname["0"]),"SYSTEMTOOL")); 

end

