rule "Windows - Applocker - Enrichment 51"

when 
    to_string($message.SourceName) == "Microsoft-Windows-AppLocker" AND
    (
        to_long($message.event_code) >= 8002 AND
        to_long($message.event_code) <= 8007
    ) OR
    ( 
        to_long($message.event_code) >= 8020 AND 
        to_long($message.event_code) <= 8025 
    )

then 
    let appcat = lookup_value(
        lookup_table: "my-applicationwhitelist", 
        key: to_string($message.appid)
    );
    set_field (
        field: "appcat", 
        value: "UNDEFINED"
    );
    set_field (
        field: "appcat", 
        value: appcat
    ); 

end

rule "Windows - Applocker - Enrichment 52"
when
    to_string($message.SourceName) == "Microsoft-Windows-AppLocker" AND 
    has_field("source")
then
    let team = lookup_value(
        lookup_table: "my-servermanagementlist", 
        key: to_string($message.source)
    );
    set_field (
        field: "appteam",
        value: "UNDEFINED"
    );
    set_field (
        field: "appteam", 
        value: team
    ); 

end

rule "Windows - Applocker - Winlogbeat - Normalization 22"

when
    to_string($message.winlogbeat_event_provider) == "Microsoft-Windows-AppLocker"

then
    set_field(
        field: "TargetDomainName",
        value: to_string($message.winlogbeat_winlog_user_domain)
    );

    set_field(
        field: "event_code",
        value: to_string($message.winlogbeat_event_code)
    );

    set_field(
        field: "user_name",
        value: to_string($message.winlogbeat_winlog_user_name)
    );

    set_field(
        field: "AccountName",
        value: to_string($message.winlogbeat_winlog_user_name)
    );

    set_field(
        field: "user_type",
        value: to_string($message.winlogbeat_winlog_user_type)
    );

    set_field(
        field: "user_id",
        value: to_string($message.winlogbeat_winlog_user_identifier)
    );

    set_field(
        field: "Channel",
        value: to_string($message.winlogbeat_winlog_channel)
    );

    set_field(
        field: "SourceName",
        value: to_string($message.winlogbeat_event_provider)
    );

end

rule "Windows - Applocker - NXLog - Normalization 21"

when
    to_string($message.SourceName) == "Microsoft-Windows-AppLocker"

then
    set_field(
        field: "TargetDomainName",
        value: to_string($message.Domain)
    );

    set_field(
        field: "event_code",
        value: to_string($message.EventID)
    );

