    );

    set_field(
        field: "source",
        value: to_string($message.collector_node_id)
    );

end

rule "Windows - AppLocker - Parsing 33" 

when 
    to_string($message.SourceName) == "Microsoft-Windows-AppLocker" AND 
    ( 
        to_long($message.event_code) >= 8020 AND 
        to_long($message.event_code) <= 8025 
    )

then
    let applocker = grok( 
        pattern: "%{WORD:appbase}[A-Z.]+%{WORD:appname} %{GREEDYDATA}", 
        value: uppercase(to_string($message.message)), only_named_captures: true 
    ); 

    let apppath = grok( 
        pattern: "%{GREEDYDATA:apppath}[.]%{WORD} %{GREEDYDATA}", 
        value: uppercase(to_string($message.message)), only_named_captures: true 
    ); 

    set_field("appextension", "APP");
    set_fields(apppath);
    set_fields(applocker); 

end

rule "Windows - AppLocker - Parsing 34" 

when 
    to_string($message.SourceName) == "Microsoft-Windows-AppLocker" AND 
    ( 
        to_long($message.event_code) >= 8002 AND 
        to_long($message.event_code) <= 8007 
    ) 

then 
    let app = regex( 
        pattern: "(([\\%]*)([A-Z0-9]+|([\\\\\\\\]*[A-Za-z0-9.]+\\\\)[A-Za-z0-9\\$]+|[A-Z]:\\\\)([\\%]*)([\\\\\\sa-zA-Z0-9\\{\\}\\$_.-]*\\\\)+([\\sa-zA-Z0-9\\$_.-]+)([.]{1})([A-Z0-9]{3,6}))", 
        value: to_string($message.message) 
    ); 

    let appname = regex( 
        pattern:"(\\\\APPDATA\\\\LOCAL\\\\TEMP\\\\)", 
        value: to_string($message.message) 
    ); 

    set_field("apppath",app["0"]);
    set_field("appbase",app["2"]);
    set_field("appname",app["6"]);
    set_field("appextension",app["8"]);

    set_field("appname", replace(to_string(appname["0"]), to_string(appname["0"]),"SYSTEMTOOL")); 

end
